import React, { useState } from "react";

export default function UpcomingProjects() {
    const [projects, setProjects] = useState([
        { id: 1, title: "Plantare copaci", description: "Plantăm copaci în parc.", image: null },
        { id: 2, title: "Curățenie plajă", description: "Curățăm plaja de gunoaie.", image: null },
    ]);

    const [newProject, setNewProject] = useState({
        title: "",
        description: "",
        image: null
    });

    // Adăugare proiect
    const handleAddProject = () => {
        if (!newProject.title || !newProject.description) return;
        const project = {
            ...newProject,
            id: Date.now() // id unic rapid
        };
        setProjects([...projects, project]);
        setNewProject({ title: "", description: "", image: null });
    };

    // Ștergere proiect
    const handleDeleteProject = (id) => {
        setProjects(projects.filter(p => p.id !== id));
    };

    return (
        <div className="content-section active">
            <h2>Proiecte Viitoare</h2>

            {/* Form pentru adăugare */}
            <div className="project-form">
                <input
                    type="text"
                    placeholder="Titlu"
                    value={newProject.title}
                    onChange={(e) => setNewProject({ ...newProject, title: e.target.value })}
                />
                <textarea
                    placeholder="Descriere"
                    value={newProject.description}
                    onChange={(e) => setNewProject({ ...newProject, description: e.target.value })}
                />
                <input
                    type="file"
                    onChange={(e) => setNewProject({ ...newProject, image: URL.createObjectURL(e.target.files[0]) })}
                />
                <button onClick={handleAddProject}>Adaugă Proiect</button>
            </div>

            {/* Listă proiecte */}
            <div className="projects-list">
                {projects.map(p => (
                    <div key={p.id} className="project-card">
                        <h3>{p.title}</h3>
                        <p>{p.description}</p>
                        {p.image && <img src={p.image} alt={p.title} style={{ maxWidth: "200px" }} />}
                        <button onClick={() => handleDeleteProject(p.id)}>Șterge</button>
                    </div>
                ))}
            </div>
        </div>
    );
}
