import React, { useState, useEffect } from "react";
import { getArticles, addArticle, deleteArticle } from "../services/api";

export default function Articles() {
    const [articles, setArticles] = useState([]);
    const [newArticle, setNewArticle] = useState({
        title: "",
        description: ""
    });
    const [loading, setLoading] = useState(true); // ca să știm când datele vin

    // Fetch articole la montare componentă
    useEffect(() => {
        async function fetchArticles() {
            try {
                const data = await getArticles();
                setArticles(data);
            } catch (error) {
                console.error("Eroare la încărcarea articolelor:", error);
            } finally {
                setLoading(false);
            }
        }
        fetchArticles();
    }, []);

    const handleAddArticle = async () => {
        if (!newArticle.title || !newArticle.description) {
            alert("Completează toate câmpurile!");
            return;
        }
        try {
            const addedArticle = await addArticle(newArticle);
            setArticles([...articles, addedArticle]);
            setNewArticle({ title: "", description: "" });
        } catch (error) {
            console.error("Eroare la adăugare articol:", error);
        }
    };

    const handleDeleteArticle = async (id) => {
        try {
            await deleteArticle(id);
            setArticles(articles.filter(a => a.id !== id));
        } catch (error) {
            console.error("Eroare la ștergere articol:", error);
        }
    };

    return (
        <div className="content-section active">
            <h2>Gestionare Articole</h2>

            <div className="article-form">
                <input
                    type="text"
                    placeholder="Titlu articol"
                    value={newArticle.title}
                    onChange={(e) => setNewArticle({ ...newArticle, title: e.target.value })}
                />
                <textarea
                    placeholder="Descriere articol"
                    value={newArticle.description}
                    onChange={(e) => setNewArticle({ ...newArticle, description: e.target.value })}
                />
                <button className="btn-primary" onClick={handleAddArticle}>Adaugă Articol</button>
            </div>

            {loading ? (
                <p>Se încarcă articolele...</p>
            ) : (
                <div className="articles-list">
                    {articles.length === 0 ? (
                        <p>Nu există articole.</p>
                    ) : (
                        articles.map(a => (
                            <div key={a.id} className="article-card">
                                <h3>{a.title}</h3>
                                <p>{a.description}</p>
                                <button className="btn" onClick={() => handleDeleteArticle(a.id)}>Șterge</button>
                            </div>
                        ))
                    )}
                </div>
            )}
        </div>
    );
}
