import React, { useState, useEffect } from "react";
import { getArticles, addArticle, deleteArticle } from "../services/api";

export default function Articles() {
    const [articles, setArticles] = useState([]);
    const [title, setTitle] = useState("");
    const [desc, setDesc] = useState("");

    useEffect(() => {
        getArticles().then(setArticles);
    }, []);

    const handleAdd = async () => {
        if (!title || !desc) {
            alert("Completează toate câmpurile!");
            return;
        }
        const newArticle = await addArticle({ title, description: desc });
        setArticles([...articles, newArticle]);
        setTitle("");
        setDesc("");
    };

    const handleDelete = async (id) => {
        await deleteArticle(id);
        setArticles(articles.filter(a => a.id !== id));
    };

    return (
        <div className="content-section active">
            <h2>Gestionare Articole</h2>
            <div className="form-row">
                <input
                    type="text"
                    placeholder="Titlu articol"
                    value={title}
                    onChange={e => setTitle(e.target.value)}
                />
                <textarea
                    placeholder="Descriere articol"
                    value={desc}
                    onChange={e => setDesc(e.target.value)}
                />
                <button className="btn-primary" onClick={handleAdd}>Adaugă</button>
            </div>

            <table>
                <thead>
                <tr>
                    <th>Titlu</th>
                    <th>Descriere</th>
                    <th>Acțiune</th>
                </tr>
                </thead>
                <tbody>
                {articles.map(a => (
                    <tr key={a.id}>
                        <td>{a.title}</td>
                        <td>{a.description}</td>
                        <td><button className="btn" onClick={() => handleDelete(a.id)}>Șterge</button></td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
}
